class Solution {
public:
    vector<vector<vector<long long>>>dp;
    long long solve(vector<vector<int>>& board,int i,int r1,int r2){
        if(i>board.size()){
            return -1e15;
        }
        if(dp[i][r1][r2]!=-1)
        return dp[i][r1][r2];
        long long ans=-1e15;
        if(r1==0){
            ans=max(ans,solve(board,i+1,r1,r2));
            for(int col=1;col<=board[0].size();col++){
                ans=max(ans,1LL*board[i-1][col-1]+solve(board,i+1,col,r2));
            }
        }
        else if(r2==0){
            ans=max(ans,solve(board,i+1,r1,r2));
            for(int col=1;col<=board[0].size();col++){
                if(col!=r1)
                ans=max(ans,1LL*board[i-1][col-1]+solve(board,i+1,r1,col));
            }
        }else{
            ans=max(ans,solve(board,i+1,r1,r2));
            for(int col=1;col<=board[0].size();col++){
                if(col!=r1 and col!=r2)
                ans=max(ans,1LL*board[i-1][col-1]);
            }
        }
        return dp[i][r1][r2]=ans;
    }
    long long maximumValueSum(vector<vector<int>>& board) {
        vector<vector<vector<long long>>>temp(board.size()+5,vector<vector<long long>>(board[0].size()+5,vector<long long>(board[0].size()+5,-1)));
        dp=temp;
        return solve(board,1,0,0);
    }
};
