import java.util.*;

class Solution {
    public long maximumValueSum(int[][] board) {
        int m = board.length, n = board[0].length;
        List<int[]>[] g = new ArrayList[m];
        long ans = Long.MIN_VALUE;
        for (int i = 0; i < m; i++) {
            g[i] = gen(board[i]);
        }
        for (int i = 0; i < m - 2; i++) {
            for (int j = i + 1; j < m - 1; j++) {
                for (int k = j + 1; k < m; k++) {
                    long tmp = getT(g[i], g[j], g[k]);
                    ans = Math.max(ans, tmp);
                }
            }
        }
        return ans;
    }

    private long getT(List<int[]> a, List<int[]> b, List<int[]> c) {
        long ans = Long.MIN_VALUE;
        for (int i = 0; i < 3; i++) {
            for (int j = 0; j < 3; j++) {
                for (int k = 0; k < 3; k++) {
                    long a1 = a.get(i)[0], a2 = b.get(j)[0], a3 = c.get(k)[0];
                    int b1 = a.get(i)[1], b2 = b.get(j)[1], b3 = c.get(k)[1];
                    if (b1 != b2 && b1 != b3 && b2 != b3) {
                        if (ans < a1 + a2 + a3) {
                            ans = a1 + a2 + a3;
                        }
                    }
                }
            }
        }
        return ans;
    }

    private List<int[]> gen(int[] rec) {
        List<int[]> lis = new ArrayList<>(rec.length);
        int n = rec.length;
        for (int i = 0; i < n; i++) {
            lis.add(new int[]{rec[i], i});
        }
        Collections.sort(lis, (a, b) -> b[0] - a[0]);
        List<int[]> list = new ArrayList<>();
        for (int i = 0; i < 3; i++) {
            list.add(lis.get(i));
        }
        return list;
    }
}
