class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& B) {
        int m = B.size();
        int n = B[0].size();
        int maxV = INT_MIN;
        for (auto& a : B) {
            for (int& b : a) {
                maxV = max(maxV, b);
            }
        }
        long hV = maxV;
        long ans = (long)B[0][0] + (long)B[1][1] + (long)B[2][2];
        for (int ix = 0; ix < m - 2; ++ix) {
            for (int iy = 0; iy < n; ++iy) {
                for (int jx = ix + 1; jx < m - 1; ++jx) {
                    for (int jy = 0; jy < n; ++jy) {
                        if (jy == iy) continue;
                        if (B[ix][iy] + B[jx][jy] + hV <= ans) continue;
                        long t;
                        if (iy != 0 && jy != 0) {
                            t = B[jx + 1][0];
                        } else if (iy != 1 && jy != 1) {
                            t = B[jx + 1][1];
                        } else {
                            t = B[jx + 1][2];
                        }
                        for (int kx = jx + 1; kx < m; ++kx) {
                            for (int ky = 0; ky < n; ++ky) {
                                if (ky == iy || ky == jy) continue;
                                t = max(t, (long)B[kx][ky]);
                            }
                        }
                        ans = max(ans, t + B[ix][iy] + B[jx][jy]);
                    }
                }
            }
        }
        return ans;
    }
};
