class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& b) {
        int m = b.size();
        int n = b[0].size();

        long long mx = LLONG_MIN;

        vector<vector<int>> bc(m, vector<int>(3, -1));
        vector<vector<long long>> bv(m, vector<long long>(3, LLONG_MIN));
        for (int i = 0; i < m; ++i) {
            vector<pair<long long, int>> cv;
            for (int j = 0; j < n; ++j) {
                cv.emplace_back(b[i][j], j);
            }
            sort(cv.rbegin(), cv.rend());

            for (int k = 0; k < 3 && k < n; ++k) {
                bv[i][k] = cv[k].first;
                bc[i][k] = cv[k].second;
            }
        }

        for (int r1 = 0; r1 < m; ++r1) {
            for (int r2 = r1 + 1; r2 < m; ++r2) {
             for (int r3 = r2 + 1; r3 < m; ++r3) {
                for (int i = 0; i < 3; ++i) {
                    for (int j = 0; j < 3; ++j) {
                        if (bc[r2][j] == bc[r1][i]) continue;                            for (int k = 0; k < 3; ++k) {
                               if (bc[r3][k] == bc[r1][i] || bc[r3][k] == bc[r2][j]) continue;

                            long long cs = bv[r1][i] + bv[r2][j] + bv[r3][k];
                                mx = max(mx, cs);
                            }
                        }


                    }
                }


            }
        }

        return mx;
    }
};
