package main

import (
	"sort"
)

type cell struct {
	i, j, value int
}

func maximumValueSum(board [][]int) int64 {
	m, n := len(board), len(board[0])
	a := make([]cell, 0, m*n)

	for i := 0; i < m; i++ {
		for j := 0; j < n; j++ {
			a = append(a, cell{i, j, board[i][j]})
		}
	}

	sort.Slice(a, func(x, y int) bool {
		return a[x].value > a[y].value
	})

	a = a[:min(m*n, (m+n)*3)]

	check := func(i, j int) bool {
		x1, y1, _ := a[i].i, a[i].j, a[i].value
		x2, y2, _ := a[j].i, a[j].j, a[j].value
		return x1 != x2 && y1 != y2
	}
	ans := -int(1e18)
	for i := 0; i < len(a); i++ {
		for j := i + 1; j < len(a); j++ {
			for k := j + 1; k < len(a); k++ {
				if check(i, j) && check(i, k) && check(j, k) {
					sum := a[i].value + a[j].value + a[k].value
					if sum > ans {
						ans = sum
					}
				}
			}
		}
	}

	return int64(ans)
}
