class Solution {
public:

    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size();
        int n = board[0].size();
        vector<vector<int>> g(m, vector<int>(3, -1));
        vector<vector<long long>> v(m, vector<long long>(3, LLONG_MIN));
        long long maxi = LLONG_MIN;
        for (int i = 0; i < m; ++i) {
            vector<pair<long long, int>> x;
            for (int j = 0; j < n; ++j)  x.emplace_back(board[i][j], j);
            sort(x.rbegin(), x.rend()); 
            for (int k = 0; k < 3 && k < n; ++k) {
                v[i][k] = x[k].first;
                g[i][k] = x[k].second;
            }
        }
        for (int r1 = 0; r1 < m; ++r1) {
            for (int r2 = r1 + 1; r2 < m; ++r2) {
                for (int r3 = r2 + 1; r3 < m; ++r3) {
                    for (int i = 0; i < 3; ++i) {
                        for (int j = 0; j < 3; ++j) {
                            if (g[r2][j] == g[r1][i]) continue;
                            for (int k = 0; k < 3; ++k) {
                                if (g[r3][k] == g[r1][i] || g[r3][k] == g[r2][j]) continue;
                                long long c = v[r1][i] + v[r2][j] + v[r3][k];
                                maxi = max(maxi, c);
                            }
                        }
                    }
                }
            }
        }
        return maxi;
    }
};
