class Solution {
public:

    long long maximumValueSum(vector<vector<int>>& board) {
       int n=board.size();
        int m=board[0].size();
         long long rowmax[n][m];
         for(int i=0;i<m;i++){
             for(int j=0;j<n;j++)
             {
                 if(j==0)
                     rowmax[j][i]=board[j][i];
                 else
                     rowmax[j][i]=max((long long)board[j][i],rowmax[j-1][i]);


             }
         }
        long long dp[n][m][m];
         for(int j1=0;j1<m;j1++){
             for(int j2=0;j2<m;j2++)
                 dp[0][j1][j2]=-10000000000000000;
         }
          for(int i=1;i<n;i++)
          {
              for(int j1=0;j1<m;j1++){
                  for(int j2=0;j2<m;j2++)
                  {
                    if(j1!=j2)
                    {dp[i][j1][j2]=board[i][j2]+rowmax[i-1][j1];
                     dp[i][j1][j2]=max(dp[i-1][j1][j2],dp[i][j1][j2]);
                    }

                 }
              }
          }
        long long ans=-1000000000000000;
        for(int i=1;i<n;i++)
          {
              for(int j1=0;j1<m;j1++){
                  for(int j2=0;j2<m;j2++)
                  {
                      if(j1!=j2){
                    for(int j3=0;j3<m;j3++)
                    {   if(j3!=j1&&j3!=j2)
                        ans=max((long long)board[i][j3]+dp[i-1][j1][j2],ans);
                    }
                    }
                 }
              }
          }
        return ans;

    }
};
