class Solution {
    long ans=Long.MIN_VALUE;
 public static void r(int[][] array) {
        int start = 0;
        int end = array.length - 1;

        // Reverse the order of the rows
        while (start < end) {
            int[] temp = array[start];
            array[start] = array[end];
            array[end] = temp;
            start++;
            end--;
        }
    }
    public long maximumValueSum(int[][] board) {
        int n=board.length;
        int m=board[0].length;
        int l=n*m;
        int[][]arr=new int[l][3];
        int x=0;
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                arr[x][0]=board[i][j];
                arr[x][1]=i;
                arr[x][2]=j;
                x++;

            }
        }
        Arrays.sort(arr, Comparator.comparingDouble(o -> o[0]));
        long ans=(long)arr[1][0]+(long)arr[0][0]+(long)arr[2][0];
        r(arr);

         for(int i=0;i<=Math.min(3*(n+m),l-1);i++){
          for(int j=i+1;j<=Math.min(3*(n+m),l-1);j++){
             for(int k=j+1;k<=Math.min(3*(n+m),l-1);k++){
                if(arr[i][1]!=arr[j][1]&& arr[j][1]!=arr[k][1] && arr[i][1]!=arr[k][1]){
                    if(arr[i][2]!=arr[j][2]&& arr[j][2]!=arr[k][2] && arr[i][2]!=arr[k][2]){
                        long a=(long)arr[i][0]+(long)arr[j][0]+(long)arr[k][0];
                        ans=Math.max(ans,a);

                    }
                }

             }
          }
         }
return ans;

    }
}
