class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
       int n=board.size();
        int m=board[0].size();
        vector<vector<pair<int,pair<int,int>>>>bo(n);

        for(int i=0;i<n;i++)
        {
            for(int j=0;j<m;j++)
            {
                bo[i].push_back({board[i][j],{i,j}});
            }
            sort(bo[i].rbegin(),bo[i].rend());
        }
        vector<pair<long long int,pair<int,int>>>v;
        for(int i=0;i<n;i++)
        {  
            v.push_back(bo[i][0]);
            v.push_back(bo[i][1]);
            v.push_back(bo[i][2]);

        }
        long long int ans=-1e10;
        sort(v.rbegin(),v.rend());
        int n1=v.size();
        for(int i=0;i<n1;i++)
        {
            for(int j=i+1;j<n1;j++)
            {
                for(int k=j+1;k<n1;k++)
                {
                    if(v[i].second.first!=v[j].second.first && v[i].second.first!=v[k].second.first
                    && v[k].second.first!=v[j].second.first)
                    {
                        if(v[i].second.second!=v[j].second.second && v[i].second.second!=v[k].second.second
                    && v[k].second.second!=v[j].second.second)
                    {
                        ans=max(ans,v[i].first+v[j].first+v[k].first);
                    }
                    }
                }
            }
        }

      return ans;

    }
};
