fmax = lambda x, y: x if x > y else y

class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        m, n = len(board), len(board[0])
        arr = [[] for _ in range(m)]
        ans = -inf
        for i in range(m):
            arr[i] = sorted(range(n), key = lambda j: -board[i][j])[ : 3]
        for x1 in range(m):
            for y1 in arr[x1]:
                for x2 in range(x1 + 1, m):
                    if x2 == x1:
                        continue
                    for y2 in arr[x2]:
                        if y2 == y1:
                            continue
                        for x3 in range(x2 + 1, m):
                            if x3 == x1 or x3 == x2:
                                continue
                            for y3 in arr[x3]:
                                if y3 == y1 or y3 == y2:
                                    continue
                                ans = fmax(ans, board[x1][y1] + board[x2][y2] + board[x3][y3])
        return ans
