class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int rows = board.size();
        int cols = board[0].size();
        long long maxSum = LLONG_MIN;
        vector<vector<pair<long long, long long>>> topThreeValues(rows, vector<pair<long long, long long>>(3, {-1, -1}));

        for (int i = 0; i < rows; ++i) {
            vector<pair<long long, long long>> valueIndexPairs;
            for (int j = 0; j < cols; ++j) {
                valueIndexPairs.emplace_back(board[i][j], j);
            }
            sort(valueIndexPairs.rbegin(), valueIndexPairs.rend());

            for (int k = 0; k < 3 && k < cols; ++k) {
                topThreeValues[i][k] = valueIndexPairs[k];
            }
        }

        for (int row1 = 0; row1 < rows; ++row1) {
            for (int row2 = row1 + 1; row2 < rows; ++row2) {
                for (int row3 = row2 + 1; row3 < rows; ++row3) {
                    for (int i = 0; i < 3; ++i) {
                        for (int j = 0; j < 3; ++j) {
                            if (topThreeValues[row2][j].second == topThreeValues[row1][i].second) continue;
                            for (int k = 0; k < 3; ++k) {
                                if (topThreeValues[row3][k].second == topThreeValues[row1][i].second || topThreeValues[row3][k].second == topThreeValues[row2][j].second) continue;

                                long long currentSum = topThreeValues[row1][i].first + topThreeValues[row2][j].first + topThreeValues[row3][k].first;
                                maxSum = max(maxSum, currentSum);
                            }
                        }
                    }
                }
            }
        }

        return maxSum;
    }
};
