#define ll long long 
class Solution {
public:
long long maximumValueSum(vector<vector<int>>& board) {
	ll m= board[0].size(), n= board.size();
	vector<vector<pair<ll,ll>>> mxVal(m);
	for(int i=0;i<m;i++){
		vector<pair<ll,ll>> tm;
		for(int j=0;j<n;j++){
			tm.push_back({board[j][i], j});
		}
		sort(tm.begin(), tm.end(), greater<pair<ll,ll>>());
		for(int j=0;j<3;j++){
			mxVal[i].push_back({tm[j]});
		}
	}

	ll ans=-1e18;
	for(int i=0;i<m;i++){
		for(int j=0;j<m;j++){
			if(j!=i){
				for(int k=0;k<m;k++){
					if(j!=k && i!=k){
						ans = max(ans, cal(mxVal[i], mxVal[j], mxVal[k]));
					}
				}
			}
		}
	}
	return ans;
}

ll cal(vector<pair<ll,ll>>&a,vector<pair<ll,ll>>&b,vector<pair<ll,ll>>&c){
	ll ans=-1e18;
	for(int i=0;i<3;i++){
		for(int j=0;j<3;j++){
			for(int k=0;k<3;k++){
				if(a[i].second!=b[j].second && a[i].second!=c[k].second && c[k].second!=b[j].second){
					ans= max(ans, a[i].first+b[j].first+c[k].first);
				}
			}
		}
	}
	return ans;
}
};
