#define MP        make_pair
#define PB        push_back
#define fi         first
#define se         second

class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size(), m = board[0].size();

        long long int val[505][505];
        // val[ignore this col][ignore this col]
        for(int i = 0; i <= m; i++) {
         for(int j = 0; j <= m; j++) val[i][j] = -1e15;
        }

     long long int  ans = -1e15;

     priority_queue<pair<long long int, int>> s[m];

     for(int i = 0; i < n; i++) {
        multiset<long long int> ns;

        for(int j = 0; j < m; j++) {
            ns.insert(board[i][j]);
        }

        for(int j = 0; j < m; j++) {
            ns.erase(ns.find(board[i][j]));
            for(int k = 0; k < j; k++) {
                ns.erase(ns.find(board[i][k]));
                if(ns.size())
                ans = max(ans, *(--ns.end()) + val[j][k]);
                ns.insert(board[i][k]);
            }

            ns.insert(board[i][j]);
        }


        for(int j = 0; j < m; j++) {
            s[j].push({board[i][j], i});

            for(int k = 0; k < j; k++) {
                if(s[j].top().se != s[k].top().se) {
                    val[j][k] = max(val[j][k], s[j].top().fi + s[k].top().fi);
                }

                if(s[j].size() > 1) {
                    pair<long long int, int> temp = s[j].top();
                    s[j].pop();
                    if(s[j].top().se != s[k].top().se) {
                        val[j][k] = max(val[j][k], s[j].top().fi + s[k].top().fi);
                    }
                    s[j].push(temp);
                }

                if(s[k].size() > 1) {
                    pair<long long int, int> temp = s[k].top();
                    s[k].pop();
                    if(s[j].top().se != s[k].top().se) {
                        val[j][k] = max(val[j][k], s[j].top().fi + s[k].top().fi);
                    }
                    s[k].push(temp);
                }
            }
        }

     }

     return ans;


    }
};
