class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n=board.size(),m=board[0].size();
        map<int,set<pair<int,int>>>v;

        for(int i=0;i<n;i++)
        {
            for(int j=0;j<m;j++)
            {
                v[i].insert({-board[i][j],j});
            }

        }


        long long ans=-1e18;


        for(int i=0;i<n;i++)
        {
            for(int j=0;j<n;j++)
            {
                for(int k=0;k<n;k++)
                {
                    if(i==j || j==k || i==k)
                    {
                        continue;
                    }


                    long long val=0;

                    auto it=v[i].begin();
                    val-=(v[i].begin())->first;

                    // cout<<"v"<<val<<endl;
                    auto it1=v[j].begin();

                    if(it1->second==it->second)
                    {
                        it1++;

                    }
                     if(it1!=v[j].end())
                    {
                         val-=(it1)->first;
                    }

                     // cout<<"v"<<val<<endl;
                    auto it2=v[k].begin();

                    if(it2->second==it1->second || it2->second==it->second)
                    {
                        it2++;
                        if(it2!=v[k].end() && (it2->second==it1->second || it2->second==it->second))
                        {
                            it2++;
                        }


                    }

                    if(it2!=v[k].end())
                    {
                         val-=(it2)->first;
                    }

                   // cout<<i<<" "<<j<<" "<<k<<" "<<val<<endl;

                    ans=max(ans,val);
                }
            }
        }

        return ans;
    }
};
