from itertools import combinations, permutations

class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        m, n = len(board), len(board[0])
        mat = [sorted((x, i) for i, x in enumerate(row)) for row in board]

        ans = -inf
        for i, j, k in combinations(list(range(m)), 3):
            for a, b, c in permutations([i, j, k]):
                s = 0
                vis = set()
                for t in [a, b, c]:
                    for v in range(n - 1, -1, -1):
                        if mat[t][v][1] not in vis:
                            vis.add(mat[t][v][1])
                            s += mat[t][v][0]
                            break

                ans = max(ans, s)

        return ans
