class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m=board.size();
        int n=board[0].size();

        vector<vector<pair<int,int>>>vec(m+1,vector<pair<int,int>>(3));
        for(int i=0;i<m;i++){
            for(int j=0;j<3;j++){
               vec[i][j]={-1,-1};
            }
        }
        for(int i=0;i<m;i++){
            pair<int,int>mx1;
            pair<int,int>mx2;
            pair<int,int>mx3;
            priority_queue<pair<int,int>>pq;
            for(int j=0;j<n;j++){
               pq.push({board[i][j],j});
            }
            mx1=pq.top();
            pq.pop();
            mx2=pq.top();
            pq.pop();
            mx3=pq.top();
            pq.pop();
            vec[i][0]=mx1;
            vec[i][1]=mx2;
            vec[i][2]=mx3;
        }
        long long ans=LONG_LONG_MIN;
        for(int r1=0;r1<m;r1++){
            for(int r2=r1+1;r2<m;r2++){
                for(int r3=r2+1;r3<m;r3++){
                    long long tempans=0;
                    for(int a=0;a<3;a++){
                        for(int b=0;b<3;b++){
                            for(int c=0;c<3;c++){
                               int ind1=vec[r1][a].second;
                               int ind2=vec[r2][b].second;
                               int ind3=vec[r3][c].second;
                               if(ind1!=ind2&&ind2!=ind3&&ind3!=ind1){
                                 long long val1=vec[r1][a].first;
                                 long long val2=vec[r2][b].first;
                                 long long val3=vec[r3][c].first;
                                 tempans=val1+val2+val3;
                                 ans=max(ans,tempans);
                               }
                            }
                        }
                    }

                }
            }
        }
        return ans;
    }
};
