class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size(), m = board[0].size();
        long long ans = (long long) INT_MIN * INT_MAX;
        vector<bool> visited(m);

        vector<vector<int>> mxs(n, vector<int>(3, INT_MIN));
        vector<vector<int>> idxs(n, vector<int>(3, 0));
        for(int i = 0; i < n; i++) {
            for(int j = 0; j < m; j++) {
                if(board[i][j] >= mxs[i][0]) {
                    mxs[i][2] = mxs[i][1];
                    idxs[i][2] = idxs[i][1];
                    mxs[i][1] = mxs[i][0];
                    idxs[i][1] = idxs[i][0];
                    mxs[i][0] = board[i][j];
                    idxs[i][0] = j;
                }
                else {
                    if(board[i][j] >= mxs[i][1]) {
                        mxs[i][2] = mxs[i][1];
                        idxs[i][2] = idxs[i][1];
                        mxs[i][1] = board[i][j];
                        idxs[i][1] = j;
                    }
                    else if(board[i][j] >= mxs[i][2]) {
                        mxs[i][2] = board[i][j];
                        idxs[i][2] = j;
                    }
                }
            }
        }

        function<void(int, int, long long)> dfs = [&](int x, int t, long long res) -> void {
            if(x == 3) {
                ans = max(ans, res);
                return;
            }

            for(int i = t; i < n - 2 + x; i++) {
                for(int j = 0; j < 3; j++) {
                    if(visited[idxs[i][j]]) continue;
                    visited[idxs[i][j]] = true;
                    res += board[i][idxs[i][j]];
                    dfs(x + 1, i + 1, res);
                    visited[idxs[i][j]] = false;
                    res -= board[i][idxs[i][j]];
                }
            }
        };

        dfs(0, 0, 0);

        return ans;
    }
};
