from typing import List, Tuple

class Solution:
    def maximumValueSum(self, grid: List[List[int]]) -> int:
        rows = len(grid)
        cols = len(grid[0])

        row_max_values = [[(float('-inf'), -1) for _ in range(3)] for _ in range(rows)]

        for r in range(rows):
            column_values = []
            for c in range(cols):
                column_values.append((grid[r][c], c))
            column_values.sort(reverse=True)
            for k in range(min(3, cols)):
                row_max_values[r][k] = column_values[k]

        max_sum = float('-inf')
        for r1 in range(rows):
            for r2 in range(r1 + 1, rows):
                for r3 in range(r2 + 1, rows):
                    for i1 in range(3):
                        for i2 in range(3):
                            if row_max_values[r1][i1][1] == row_max_values[r2][i2][1]:
                                continue
                            for i3 in range(3):
                                if (row_max_values[r1][i1][1] == row_max_values[r3][i3][1] or
                                    row_max_values[r2][i2][1] == row_max_values[r3][i3][1]):
                                    continue

                                current_sum = row_max_values[r1][i1][0] + row_max_values[r2][i2][0] + row_max_values[r3][i3][0]
                                max_sum = max(max_sum, current_sum)

        return max_sum
