class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size();
        int n = board[0].size();
        long long res = -1e18;
        vector<vector<pair<long long,int>>>rmx(m);
        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++) rmx[i].push_back({board[i][j],j});
            sort(rmx[i].rbegin(),rmx[i].rend());
        }

        for(int i1=0;i1<m;i1++){
            for(int i2=0;i2<m;i2++){
                for(int i3=0;i3<m;i3++){
                    if(i1==i2 or i2==i3 or i1==i3) continue;
                    for(int j1=0;j1<3;j1++){
                        for(int j2=0;j2<3;j2++){
                            for(int j3=0;j3<3;j3++){
                                if(rmx[i1][j1].second==rmx[i2][j2].second or rmx[i2][j2].second==rmx[i3][j3].second or rmx[i1][j1].second==rmx[i3][j3].second) continue;
                                    res = max(res, rmx[i1][j1].first+rmx[i2][j2].first+rmx[i3][j3].first);
                            }
                        }
                    }
                }
            }
        }
        return res;


    }
};
