long long dp[105][105][105];
class Solution {
public:
    int n,m;

    long long solver(int i,int j,int k,vector<vector<int>>& arr ){
        if(i == n){
            return -1e13;
        }
        if(dp[i][j+1][k+1] != -1  ) return dp[i][j+1][k+1];
        long long ans =solver(i+1,j,k,arr);
        for(int t = 0; t < m; t++){
            if(t != k && t != j){
                if(k == -1 || j == -1){
                    ans = max(arr[i][t]+ solver(i+1, t, j,arr),ans );
                    continue;
                } 
                ans = max(ans, (long long)arr[i][t]);
            }
        }
        return dp[i][j+1][k+1]=ans;

    }
    long long maximumValueSum(vector<vector<int>>& brr) {
        n=brr.size();
        m=brr[0].size();
        memset(dp,-1,sizeof(dp));
        return solver(0,-1,-1,brr);
     }
};
