class Solution {
public:

    long long func(int i,int j,int k,vector<vector<pair<int,int>>> &rm){
         long long ans=-3e9;
         for(int ii=0;ii<3;ii++){
            for(int jj=0;jj<3;jj++){
                for(int kk=0;kk<3;kk++){
                    int x,y,z;
                    x=rm[i][ii].second;
                    y=rm[j][jj].second;
                    z=rm[k][kk].second;
                    long long xx,yy,zz;
                    xx=rm[i][ii].first;
                    yy=rm[j][jj].first;
                    zz=rm[k][kk].first;
                   if(x!=y && y!=z && z!=x){
                    ans=max(xx+yy+zz,ans);
                   }
                }
            }
         }
         return ans;
    }

    long long maximumValueSum(vector<vector<int>>& a) {
         int n=a.size();
         int m=a[0].size();
         vector<vector<pair<int,int>>> rm(n);
         for(int i=0;i<n;i++){
            vector<pair<int,int>> temp;
            for(int j=0;j<m;j++)temp.push_back({a[i][j],j});
            sort(temp.begin(),temp.end());
            reverse(temp.begin(),temp.end());
            for(int j=0;j<3;j++){
               rm[i].push_back({temp[j].first,temp[j].second});
            }
         }
         long long ans=-3e9;
         for(int i=0;i<n;i++){
            for(int j=i+1;j<n;j++){
                for(int k=j+1;k<n;k++){
                    ans=max(func(i,j,k,rm),ans);
                }
            }
         }

        return ans;
    }
};
