class Solution {
    public static long maximumValueSum(int[][] board) {
        int len = board.length;
        long ans = Long.MIN_VALUE;
        int[][][] arr = new int[len][][];
        for (int i = 0; i < len; i++) {
            arr[i] = max3(board[i]);
        }

        for (int i = 0; i < len; i++) {
            for (int j = 0; j < len; j++) {
                if (i == j) continue;
                for (int k = 0; k < len; k++) {
                    if (i == k || j == k) continue;
                    int[][] max3i = arr[i];
                    int[][] max3j = arr[j];
                    int[][] max3k = arr[k];
                    long total = Long.MIN_VALUE;
                    for (int l = 0; l < 3; l++) {
                        for (int m = 0; m < 3; m++) {
                            if (max3i[l][1] == max3j[m][1]) continue;
                            for (int n = 0; n < 3; n++) {
                                if (max3i[l][1] == max3k[n][1] || max3j[m][1] == max3k[n][1]) continue;
                                total = Long.max(total, ((long) max3i[l][0]) + max3j[m][0] + max3k[n][0]);
                            }
                        }
                    }
                    ans = Long.max(ans, total);
                }
            }
        }
        return ans;
    }

    private static int[][] max3(int[] arr) {
        int n = arr.length;
        int[][] ret = new int[3][2];
        ret[0][0] = arr[0];
        ret[0][1] = 0;
        ret[1][0] = Integer.MIN_VALUE;
        ret[2][0] = Integer.MIN_VALUE;
        for (int i = 1; i < n; i++) {
            if (arr[i] > ret[0][0]) {
                ret[2][0] = ret[1][0];
                ret[2][1] = ret[1][1];

                ret[1][0] = ret[0][0];
                ret[1][1] = ret[0][1];

                ret[0][0] = arr[i];
                ret[0][1] = i;
            } else if (arr[i] > ret[1][0]) {
                ret[2][0] = ret[1][0];
                ret[2][1] = ret[1][1];

                ret[1][0] = arr[i];
                ret[1][1] = i;
            } else if (arr[i] > ret[2][0]) {
                ret[2][0] = arr[i];
                ret[2][1] = i;
            }
        }
        return ret;
    }
}
