class Solution {
    int n, m;
    vector<vector<int>> pre;
public:
    long long solve(vector<vector<int>> &board, int f , int s, int t, int cnt, int idx, vector<vector<vector<long long>>> &dp, vector<vector<long long>> &pre){
        if(cnt == 0){
            return 0;
        }

        if(idx >= n) return -1e12;

        if(f!= -1 && s!=-1){
            long long res = -1e10;
            for(int i =0 ; i < m; i++){
                if(i != f && s!=i){
                    res = max(res, pre[idx][i]);
                } 
            }

            return res;
        }


        // cout<<f<<" "<<s<<" "<<cnt<< idx
        if(dp[f+1][s+1][idx] != -1) return dp[f+1][s+1][idx];

        long long op = -1e12;
        op = solve(board , f, s, t, cnt, idx+1, dp, pre);
        for(int i = 0; i< m; i++){
            if( i!= f && i!=s && i!= t){
                if(f == -1){
                    op = max(op, board[idx][i] + solve(board, i , s, t , cnt-1, idx+1, dp, pre));
                }
                else if(s == -1){
                    op = max(op, board[idx][i] + solve(board, f , i, t , cnt-1, idx+1, dp, pre));
                }
                else op = max(op, board[idx][i] + solve(board, f, s, i , cnt - 1, idx+1, dp, pre));
            }
        }

        return dp[f+1][s+1][idx] =  op;
    }

    long long maximumValueSum(vector<vector<int>>& board) {
        n = board.size();
        m = board[0].size();
        vector<vector<long long>> pre(n, vector<long long> (m, -1e10));
        for(int i = n-1; i>=0; i--){
            for(int j = 0 ; j< m ; j++){
                pre[i][j] = board[i][j];
                if(i < n-1) pre[i][j] = max(pre[i][j], pre[i+1][j]);
            }
        }

        vector<vector<vector<long long>>> dp(m+2 , vector<vector<long long>> (m+2, vector<long long> (n+2, -1)));
        return solve(board, -1, -1, -1, 3, 0, dp, pre);
    }
};
