class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n=board.size(), m=board[0].size();
        vector<vector<int>> r(n, vector<int> (3,-1)), c(m, vector<int> (3,-1));
        for(int i=0; i<n; i++){
            for(int j=0; j<m; j++){
                if(r[i][0]==-1 || board[i][j]>board[i][r[i][0]]){
                    r[i][2]=r[i][1];
                    r[i][1]=r[i][0];
                    r[i][0]=j;
                }
                else if(r[i][1]==-1 || board[i][j]>board[i][r[i][1]]){
                    r[i][2]=r[i][1];
                    r[i][1]=j;
                }
                else if(r[i][2]==-1 || board[i][j]>board[i][r[i][2]]){
                    r[i][2]=j;
                }
            }
        }
        for(int j=0; j<m; j++){
            for(int i=0; i<n; i++){
                if(c[j][0]==-1 || board[i][j]>board[c[j][0]][j]){
                    c[j][2]=c[j][1];
                    c[j][1]=c[j][0];
                    c[j][0]=i;
                }
                else if(c[j][1]==-1 || board[i][j]>board[c[j][1]][j]){
                    c[j][2]=c[j][1];
                    c[j][1]=i;
                }
                else if(c[j][2]==-1 || board[i][j]>board[c[j][2]][j]){
                    c[j][2]=i;
                }
            }
        }
        int  b=-1, d=-1;
        long long ans=-1e12;
        for(int i=0; i<n; i++){
            //a=i;
            for(auto& it1: r[i]){
                b=it1;
                for(int j=0; j<n; j++){
                    if(i==j) continue;
                    //c=j;
                    for(auto& it2: r[j]){
                        if(it2!=b) d=it2;
                        else continue;
                        for(int k=0; k<n; k++){
                            if(k==i || k==j) continue;
                            for(auto& it3: r[k]){
                                if(it3!=b && it3!=d){
                                    ans=max(ans, 1ll*(1ll*board[i][b]+1ll*board[j][d]+1ll*board[k][it3]));
                                }
                            }
                        }
                    }
                }
            }
        }
        return ans;
    }
};
