class Solution {
public:
    typedef long long ll;

    static bool cmp(pair<int, pair<int, int>>& a, pair<int, pair<int, int>>&b){
        return a.first < b.first;
    }


ll maximumValueSum(vector<vector<int>>& board) {
    int n = board.size();
    int m = board[0].size();
    vector<vector<pair<int,pair<int,int>>>> aboard(n, vector<pair<int,pair<int,int>>>(m));
    for(int i=0; i<n; i++){
        for(int j=0; j<m; j++){
            aboard[i][j] = {board[i][j], {i,j}};
        }
    }
    vector<pair<int,pair<int,int>>> list;
    for(int i=0; i<n; i++){
        sort(aboard[i].rbegin(), aboard[i].rend(), cmp);
        list.push_back({aboard[i][0].first, {aboard[i][0].second.first,aboard[i][0].second.second}});
        list.push_back({aboard[i][1].first, {aboard[i][1].second.first,aboard[i][1].second.second}});
        list.push_back({aboard[i][2].first, {aboard[i][2].second.first,aboard[i][2].second.second}});
    }
    ll ans = -1e10;
    for(int i=0; i<list.size(); i++){
        for(int j=i+1; j<list.size(); j++){
            for(int k=j+1; k<list.size(); k++){
                if(list[i].second.first == list[j].second.first || list[j].second.first == list[k].second.first || list[i].second.first == list[k].second.first) continue;
                if(list[i].second.second == list[j].second.second || list[j].second.second == list[k].second.second || list[i].second.second == list[k].second.second) continue;


                    ans = max(ans, (ll)list[i].first + list[j].first + list[k].first);

            }
        }
    }
    return ans;


}
};
