class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size(), m = board[0].size();
        vector<set<pair<int,int>>> check(n);
        for(long long i=0;i<board.size();i++) {
            for(long long j=0;j<board[0].size();j++) {
                check[i].insert(make_pair(board[i][j], j));
                if(check[i].size() > 3) check[i].erase(check[i].begin());
            }
        }
        long long ans = LLONG_MIN;
        for(int i=0;i<n;i++) {
            for(int j=i+1;j<n;j++) {
                for(int k=j+1;k<n;k++) {
                    for(auto x: check[i]) {
                        for(auto y: check[j]) {
                            for(auto z: check[k]) {
                                if(x.second != y.second && y.second != z.second && x.second != z.second) {
                                long long val = x.first;
                                val += y.first;
                                val += z.first;
                                ans = max(ans, (long long)(val));
                                }
                            }
                        }
                    }
                }
            }
        }
        return ans;   
    }
};
