    // val2 = data[r1][n-2].first;
    // val2 = val2 + max(data[r2][n-1].first + data[r3][n-3].first, data[r2][n-3].first + data[r3][n-1].first);

    // val3 = data[r1][n-1].first;
    // val3 = val3 + max(data[r2][n-2].first + data[r3][n-3].first, data[r2][n-3].first + data[r3][n-2].first);

    // maxval = max(maxval,val1);
