    for (int i = 0; i < 3; i++) {
                        for (int j = 0; j < 3; j++) {
                            for (int k = 0; k < 3; k++) {

                                if (data[r1][i+n-3].second != data[r2][j+n-3].second && data[r2][j+n-3].second != data[r3][k+n-3].second && data[r1][i+n-3].second != data[r3][k+n-3].second) {
