import (
	"slices"
)

func maximumValueSum(board [][]int) int64 {
	maxes := make([][]int, len(board))
	for i, arr := range board {
		maxes[i] = max3(arr)
	}

	var mx int64 = -101010101010
	for i := 0; i < len(board); i++ {
		for j := i + 1; j < len(board); j++ {
			for k := j + 1; k < len(board); k++ {
				for ri := 0; ri < 3; ri++ {
					for rj := 0; rj < 3; rj++ {
						for rk := 0; rk < 3; rk++ {
							if mi, mj, mk := maxes[i][ri], maxes[j][rj], maxes[k][rk]; mi != mj && mj != mk && mi != mk {
								mx = max(mx, int64(board[i][mi])+int64(board[j][mj])+int64(board[k][mk]))
							}
						}
					}
				}
			}
		}
	}

	return mx
}

func max3(arr []int) []int {
	inc := []int{0, 1, 2}
	slices.SortFunc(inc, func(i, j int) int { return arr[j] - arr[i] })

	for i := 3; i < len(arr); i++ {
		item := arr[i]
		if arr[inc[0]] < item {
			inc[0], inc[1], inc[2] = i, inc[0], inc[1]
		} else if arr[inc[1]] < item {
			inc[1], inc[2] = i, inc[1]
		} else if arr[inc[2]] < item {
			inc[2] = i
		}
	}

	return inc
}
