class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int col = board.size();
        int row = board[0].size();

        vector<vector<pair<int, int>>> rowarr(col, vector<pair<int, int>>(3, {INT_MIN, -1}));

        for (int i = 0; i < col; ++i) {
            vector<pair<int, int>> col;
            for (int j = 0; j < row; ++j) {
                col.push_back({board[i][j], j});
            }
            sort(col.rbegin(), col.rend()); 
            for (int k = 0; k < min(3, row); ++k) {
                rowarr[i][k] = col[k];
            }
        }

        long long ans = LLONG_MIN; 
        for (int r1 = 0; r1 < col; ++r1) {
            for (int r2 = r1 + 1; r2 < col; ++r2) {
                for (int r3 = r2 + 1; r3 < col; ++r3) {
                    for (int i1 = 0; i1 < 3; ++i1) {
                        for (int i2 = 0; i2 < 3; ++i2) {
                            if (rowarr[r1][i1].second == rowarr[r2][i2].second) continue;
                            for (int i3 = 0; i3 < 3; ++i3) {
                                if (rowarr[r1][i1].second == rowarr[r3][i3].second ||
                                    rowarr[r2][i2].second == rowarr[r3][i3].second) continue;

                                long long sum = (long long)rowarr[r1][i1].first + rowarr[r2][i2].first + rowarr[r3][i3].first;
                                ans = max(ans, sum);
                            }
                        }
                    }
                }
            }
        }

        return ans;
    }
};
