class Solution {
public:
    struct Cell {
        int val;
        int m;
        int n;
        bool operator<(const Cell& c2) const {
            return val > c2.val; // greater
        }
    };

    bool Check(const vector<Cell>& vc, int i, int j, int k) {
        //if (i==0&&j==4&&k==5) {
        //    cout<<"DEBUG"<<vc[i].m<<" "<<vc[j].m<<" "<<vc[k].m<<" "<<vc[i].n<<" "<<vc[j].n<<" "<<vc[k].n<<endl;
        //}
        if (vc[i].m == vc[j].m || vc[i].m == vc[k].m || vc[j].m == vc[k].m) return false;
        if (vc[i].n == vc[j].n || vc[i].n == vc[k].n || vc[j].n == vc[k].n) return false;
        return true;
    }

    long long Sum(const vector<Cell>& vc, int i, int j, int k) {
        return (long long)vc[i].val + vc[j].val + vc[k].val;
    }

    long long maximumValueSum(vector<vector<int>>& board) {
        multiset<Cell> sc;
        int mb = board.size();
        int nb = board[0].size();
        int ssize = (mb+nb) * 2;
        //cout<<"ssize:"<<ssize<<endl;
        for (int i=0;i<mb;i++) {
            for (int j=0;j<nb;j++) {
                sc.insert({board[i][j], i, j});
                if (sc.size() > ssize) sc.erase(prev(sc.end()));
            }
        }
        vector<Cell> vc(sc.begin(), sc.end());
        //for (auto c:vc) {
        //    cout<<c.val<<" ";
        //}
        int vsize = vc.size();
        long long ans = -1e10;
        for (int i=0;i<vsize-2;i++) {
            for (int j=i+1;j<vsize-1;j++) {
                for (int k=j+1;k<vsize;k++) {
                    if (Check(vc, i, j, k)) {
                        long long total = Sum(vc, i, j, k);
                        if (total > ans) {
                            ans = total;
                            //cout<<"ans:"<<ans<<" "<<i<<' '<<j<<" "<<k<<endl;
                        }

                    }
                }
            }
        }
        return ans;
    }
};
