class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& b ) {
        vector<pair<long long,pair<int,int>>>vp;

        for(int i=0;i<b.size();i++){
            vector<pair<long long,pair<int,int>>>temp;
            for(int j=0;j<b[i].size();j++){
                temp.push_back({b[i][j],{i,j}});
            }
                sort(temp.rbegin(),temp.rend());
                vp.push_back({temp[0].first,{temp[0].second.first,temp[0].second.second}});
                vp.push_back({temp[1].first,{temp[1].second.first,temp[1].second.second}});
                vp.push_back({temp[2].first,{temp[2].second.first,temp[2].second.second}});
                if(temp.size()>3) vp.push_back({temp[3].first,{temp[3].second.first,temp[3].second.second}});
               if(temp.size()>4)  vp.push_back({temp[4].first,{temp[4].second.first,temp[4].second.second}});
        }

      long long maxi=-1e10;

      for (int i = 0; i < vp.size() - 2; i++) {
            for (int j = i + 1; j < vp.size() - 1; j++) {
                if (vp[i].second.first == vp[j].second.first || vp[i].second.second == vp[j].second.second) continue;

                for (int k = j + 1; k < vp.size(); k++) {
                    if (vp[k].second.first != vp[i].second.first && vp[k].second.first != vp[j].second.first && vp[k].second.second != vp[i].second.second && vp[k].second.second != vp[j].second.second) {
                        maxi = max(maxi, vp[i].first + vp[j].first + vp[k].first);
                    }
                }
            }
      }

        return maxi;

    }
};
