class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        long long res=-1e15;
        int end=board.size()-2;
        int n=board[0].size();
        while (end>0) {
            vector<long long> up(board[0].size(),-1e9);
            vector<long long> down(board[0].size(),-1e9);
            for (int i=0;i<n;i++){
                for (int j=0;j<end;j++){
                    if (board[j][i]>up[i]) {
                        up[i]=board[j][i];
                    }
                }
            }
            for (int i=0;i<n;i++){
                for (int j=end+1;j<board.size();j++){
                    if (board[j][i]>down[i]) {
                        down[i]=board[j][i];
                    }
                }
            }
            long long temp=0;
            for (int i=0;i<board[0].size();i++){
                int ind=i;
                long long temp=-1e15;
                for (int j=0;j<up.size();j++){
                    if (j!=ind) {
                         long long ele=up[j];
                         long long high=-1e9;
                         for (int k=0;k<down.size();k++) {
                            if (k!=j && k!=ind) {
                                if (down[k]>high) {
                                    high=down[k];
                                }
                            }
                         }
                         if (ele+high+board[end][i]>temp) {
                            temp=ele+high+board[end][i];
                         }
                    }
                }
                if (temp>res) {
                    res=temp;
                }
            }
            end--;
        }
        return res;
    }
};
