typedef long long ll;
class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        ll n = board.size(), m = board[0].size();
		// store top 3 (max values, i index) for each column
		vector<vector<pair<ll,ll>>> top3(m, vector<pair<ll,ll>>(3, {-1e11,0}));
		for(ll j = 0; j < m; j++){
			for(ll i = 0; i < n; i++){
				if(board[i][j] > top3[j][0].first){
					top3[j][2] = top3[j][1];
					top3[j][1] = top3[j][0];
					top3[j][0] = {board[i][j], i};
				}
				else if(board[i][j] > top3[j][1].first){
					top3[j][2] = top3[j][1];
					top3[j][1] = {board[i][j], i};
				}
				else if(board[i][j] > top3[j][2].first){
					top3[j][2] = {board[i][j], i};
				}
			}
		}

        // print top3 for each column
		// for(int i = 0; i < m; i++){
		// 	for(int j = 0; j < 3; j++){
		// 		cout<<top3[i][j].first<<" "<<top3[i][j].second<<" | ";
		// 	}
		// 	cout<<"\n";
		// }

		ll ans = -1e14;
		// brute force for all columns : we need to take 3 rows, one from distinct columns so that we can maximize the sum
		ll x,y,z;
		for(int i = 0; i < m-2; i++){
			// we can choose any of top 3 values from column i
			for(int ii = 0; ii<3; ii++){
				// we choose ii row from column i
				for(int j = i+1; j<m-1; j++){
					// we can choose any of top 3 values from column j
					for(int jj = 0; jj<3; jj++){
						// row of the top values should be distinct
						if(top3[i][ii].second == top3[j][jj].second) continue;
						// we choose jj row from column j
						for(int k = j+1; k<m; k++){
							// we can choose any of top 3 values from column k
							for(int kk = 0; kk<3; kk++){
								if(top3[i][ii].second == top3[k][kk].second || top3[j][jj].second == top3[k][kk].second) continue;
								// sum of the 3 chosen cells
								ll sum = top3[i][ii].first + top3[j][jj].first + top3[k][kk].first;
								ans = max(ans, sum);
							}
						}
					}
				}
			}
		}

		return ans;
    }
};
