class Solution {
public:
    #define ll long long
    #define pii pair<int, int>
    long long maximumValueSum(vector<vector<int>>& a) {
        ll ans= -1e17;
        int n = a.size();
        int m = a[0].size();
        vector<vector<int>>arr;
        for(int i=0; i<n; i++){
            for(int j=0; j<m; j++){
                arr.push_back({a[i][j], i, j});
            }
        }
        sort(arr.rbegin(), arr.rend());
        int mx = 1000;
        int z = arr.size();
        mx = min(mx, z);
        for(int i=0; i<5; i++){
            for(int j=i+1; j<mx; j++){
                if(arr[i][1]==arr[j][1] || arr[i][2]==arr[j][2]){
                    continue;
                }
                for(int k=j+1; k<mx; k++){
                    if(arr[i][1]==arr[k][1] || arr[i][2]==arr[k][2]){
                        continue;
                    }
                    if(arr[j][1]==arr[k][1] || arr[j][2]==arr[k][2]){
                        continue;
                    }
                    ll x1 = arr[i][0];
                    ll x2 = arr[j][0];
                    ll x3 = arr[k][0];
                    ll curr = x1 + x2 + x3;
                    ans = max(ans, curr);
                }
            }
        }
        return ans;

    }
};
