class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& arr) {
        int n = arr.size(), m = arr[0].size();
        vector<vector<pair<long long,int>>>v(n);
        for(int i=0;i<n;i++)
        {
            vector<pair<long long,int>>vec;
            for(int j=0;j<m;j++)
            {
                vec.push_back({arr[i][j], j});
            }
            sort(vec.begin(), vec.end());
            reverse(vec.begin(), vec.end());
            for(int j=0;j<3;j++)
            {
                v[i].push_back(make_pair(vec[j].first, vec[j].second));
            }
        }
        // for(int i=0;i<n;i++)
        // {
        //     cout<<"at i = "<<i<<endl;
        //     for(int j=0;j<3;j++)
        //     {
        //         cout<<v[i][j].first<<" "<<v[i][j].second<<" ";
        //     }
        //     cout<<endl;
        // }
        long long mx = -1e18;
        for(int i=0;i<n-2;i++)
        {
            for(int j=i+1;j<n-1;j++)
            {
                for(int k=j+1;k<n;k++)
                {
                    //mx = max(mx, v[i][0].first + v[j][1].first + v[k][2].first);
                    for(int t1=0;t1<3;t1++)
                    {
                        for(int t2=0;t2<3;t2++)
                        {
                            for(int t3=0;t3<3;t3++)
                            {
                                if(v[i][t1].second != v[j][t2].second && v[j][t2].second != v[k][t3].second && v[k][t3].second != v[i][t1].second)
                                {
                                    long long x = v[i][t1].first + v[j][t2].first + v[k][t3].first;
                                    mx = max(mx, x);
                                }

                            }
                        }
                    }
                }
            }
        }
        return mx;
    }
};
