class Item implements Comparable<Item> {
    int idx;
    long v;
    public Item(int idx, long v) {
        this.idx = idx;
        this.v = v;
    }

    public int compareTo(Item i) {
        if(i.v < v) {
            return -1;
        }
        if(i.v == v) {
            return 0;
        }
        return 1;
    }
}

class Solution {
    int g[][];
    List<List<Item>> list;
    public long maximumValueSum(int[][] g) {
        this.g = g;
        int m = g.length;
        int n = g[0].length;

        list = new ArrayList<>();
        for(int i=0; i<m; i++) {
            list.add(new ArrayList<>());
        }
        for(int i=0; i<m; i++) {
            for(int j=0; j<n; j++) {
                list.get(i).add(new Item(j, (long)g[i][j]));
            }
            Collections.sort(list.get(i));
        }

        long res = Long.MIN_VALUE;
        for(int i=0; i<m-2; i++) {
            for(int j=i+1; j<m-1; j++) {
                for(int k=j+1; k<m; k++) {
                    long r = help(i, j, k);
                    res = Math.max(res, r);
                }
            }
        }
        return res;
    }

    public long help(int x, int y, int z) {
        int n = g[0].length;
        long res = Long.MIN_VALUE;
        List<Item> l1 = list.get(x);
        List<Item> l2 = list.get(y);
        List<Item> l3 = list.get(z);
        for(int i=0; i<3; i++) {
            for(int j=0; j<3; j++) {
                for(int k=0; k<3; k++) {
                    if(ok(l1.get(i), l2.get(j), l3.get(k))) {
                        // System.out.println(l1.get(i).v + " " + l2.get(j).v + " " + l3.get(k).v);
                        res = Math.max(res, l1.get(i).v + l2.get(j).v + l3.get(k).v);
                    }
                }
            }
        }
        return res;
    }

    private boolean ok(Item x, Item y, Item z) {
        return x.idx != y.idx && x.idx != z.idx && y.idx != z.idx;
    }
}
