class Solution {
public:
    #define ll long long
    long long maximumValueSum(vector<vector<int>>& board) {
        long long ans = LLONG_MIN;

        int  m = board.size() , n = board[0].size();

        vector<vector<int>> dp(n);

        for(int j=0; j<n; j++){
            map<int,vector<int>> mp;
            priority_queue<int,vector<int>,greater<int>> pq;
            for(int i=0; i<m; i++){
                pq.push(board[i][j]);
                mp[board[i][j]].push_back(i);

                if(pq.size() > 3){pq.pop();}
            }

            vector<int> nums={0,0,0};
            nums[0] = mp[pq.top()].back();
            mp[pq.top()].pop_back();
            pq.pop();
            nums[1] = mp[pq.top()].back();
            mp[pq.top()].pop_back();
            pq.pop();
            nums[2] = mp[pq.top()].back();
            mp[pq.top()].pop_back();
            pq.pop();
            dp[j] = nums;

        }



        for(int i=0; i<n; i++){
            for(int j=i+1; j<n; j++){
                for(int k=j+1; k<n; k++){

                   vector<int> v1= dp[i] , v2=dp[j] , v3= dp[k];

                    for(int li=0; li<3; li++){
                        int ci = v1[li];
                        for(int lj=0; lj<3; lj++){
                            int cj = v2[lj];
                            for(int lk=0; lk<3; lk++){

                                int ck = v3[lk];
                                if(ci == cj || cj==ck || ci == ck){continue;}
                                ans = max(ans,0LL+ board[ci][i] + board[cj][j] + board[ck][k]);
                            }
                        }
                    }


                }
            }
        }
        return ans;

    }
};
