class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& g) {
        int n = g.size(), m = g[0].size();
        vector<vector<int>> can(n, vector<int>(m, 0));
        using pii = pair<int, int>;
        for(int i = 0; i < n; i++){
            vector<pii> on;
            for(int j = 0; j < m; j++){
                on.emplace_back(g[i][j], j);
            }
            sort(on.rbegin(), on.rend());
            for(int j = 0; j < 3; j++){
                can[i][on[j].second] = 1;
            }
        }
        for(int j = 0; j < m; j++){
            vector<pii> on;
            for(int i = 0; i < n; i++){
                on.emplace_back(g[i][j], i);
            }
            sort(on.rbegin(), on.rend());
            for(int i = 0; i < 3; i++){
                can[on[i].second][j] = 1;
            }
        }
        using E = tuple<int, int, int>;
        vector<E> a;
        for(int i = 0; i < n; i++){
            for(int j = 0; j < m; j++){
                if(can[i][j]){
                    a.emplace_back(g[i][j], i, j);
                }
            }
        }
        sort(a.rbegin(), a.rend());
        int len = a.size();
        using ll = long long;
        ll res = -(1ll<<60);
        for(int i = 0; i < len; i++){
            for(int j = i + 1; j < len; j++){
                if(get<1>(a[i]) != get<1>(a[j]) && get<2>(a[i]) != get<2>(a[j])){
                    for(int k = j + 1; k < len; k++){
                        if(get<1>(a[i]) != get<1>(a[k]) && get<2>(a[i]) != get<2>(a[k])){
                            if(get<1>(a[k]) != get<1>(a[j]) && get<2>(a[k]) != get<2>(a[j])){
                                res = max(res, ll(get<0>(a[i])) + ll(get<0>(a[j])) + ll(get<0>(a[k])));
                                break;
                            }
                        }
                    }
                }
            }
        }
        return res;
    }
};
