class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        arr = []
        m, n = len(board), len(board[0])
        for i in range(m):
            for j in range(n):
                arr.append((board[i][j], i, j))
        arr.sort(reverse=True)
        ans = -math.inf
        t = m * n
        for i in range(8):
            x1, r1, c1 = arr[i]
            for j in range(i + 1, t - 1):
                x2, r2, c2 = arr[j]
                if r1 == r2 or c1 == c2:
                    continue
                for k in range(j + 1, t):
                    x3, r3, c3 = arr[k]
                    if r3 != r1 and r3 != r2 and c3 != c1 and c3 != c2:
                        ans = max(ans, x1 + x2 + x3)
                        break
        return ans
