class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& arr) {
        int m = arr.size();
        int n = arr[0].size();
        vector<tuple<int, int, int>> elements;

        for (int i = 0; i < m; ++i) {
            for (int j = 0; j < n; ++j) {
                elements.push_back(make_tuple(arr[i][j], i, j));
            }
        }

        sort(elements.rbegin(), elements.rend());

        long long ans = LLONG_MIN;
        int maxit = n + m;

        for (int i = 0; i < min(1 * maxit, (int)elements.size()); ++i) {
            auto [v1, r1, c1] = elements[i];
            for (int j = i + 1; j < min(2 * maxit, (int)elements.size()); ++j) {
                auto [v2, r2, c2] = elements[j];

                if (r1 == r2 || c1 == c2) {
                    continue;
                }

                if ((long long)v1 + (long long)v2 + (long long)get<0>(elements[min(j+1, (int)elements.size() - 1)]) <= ans) {
                    break;
                }


                for (int k = j + 1; k < min(3 * maxit, (int)elements.size()); ++k) {
                    auto [v3, r3, c3] = elements[k];

                    if (r1 != r2 && r1 != r3 && r2 != r3 && c1 != c2 && c1 != c3 && c2 != c3) {
                        long long cur = (long long)v1 + (long long)v2 + (long long)v3;
                        ans = max(ans, cur);
                    }
                }
            }
        }

        return ans;
    }
};
