class Solution:
    def maximumValueSum(self, mat: List[List[int]]) -> int:
        m , n = len(mat) , len(mat[0])
        points = []
        for i in range(m):
            for j in range(n):
                points.append((mat[i][j],i,j))

        points.sort(reverse=True)
        N = len(points)
        ans = -inf

        def dfs(i,mask_r,mask_c,ret):
            nonlocal ans
            if len(mask_r) == 3:
                ans = ret
                return
            if i == N or ret + (3-len(mask_r)) * points[i][0] <= ans:return
            val , r , c = points[i]
            dfs(i+1,mask_r,mask_c,ret)
            if r not in mask_r and c not in mask_c:
                dfs(i+1,mask_r+(r,),mask_c+(c,),ret+val)
            return

        dfs(0,tuple(),tuple(),0)
        return ans
