#define ll long long
#define pii pair<int,int>
class Solution {
public:
    long long findMax(int i,int j,vector<vector<pair<int,int>>> &vp,vector<pair<int,pair<int,int>>> &vp1){
        long long maxi = -1e17; 
        for(int a = 0; a < 3; a++){
            for(int b = 0; b < 3; b++){
                for(int c = 0; c < vp1.size(); c++){
                    pii p1 = vp[i][a], p2 = vp[j][b];
                    pair<int,pair<int,int>> p3 = vp1[c]; 
                    if(p1.second != p2.second && p2.second != p3.second.second && p1.second != p3.second.second && p3.second.first != i and p3.second.first != j){
                        // cout<<i<<" "<<j<<" "<<vp1[c].second<<endl; 
                        // cout<<p1.second<< " "<<p2.second<<" "<<p3.second<<endl<<endl;
                        maxi = max(maxi , (ll)p1.first + (ll)p2.first + (ll)p3.first); 
                    }
                }
            }
        }
        return maxi; 
    }
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size(); 
        int m = board[0].size(); 
        vector<vector<pair<int,int>>> vp(n,vector<pair<int,int>>(3)); 
        priority_queue<pair<int,int>,vector<pair<int,int>>,greater<>> pq; 
        priority_queue<pair<int,pair<int,int>>,vector<pair<int,pair<int,int>>>,greater<>> pq1; 
        for(int i = 0; i < n; i++){

            for(int j = 0; j< m;j++){
                if(pq.size() < 3)pq.push({board[i][j],j}); 
                else if(pq.top().first < board[i][j]){
                    pq.pop(); 
                    pq.push({board[i][j],j}); 
                }
            }
            vp[i][2] = pq.top(); pq.pop();
            vp[i][1] = pq.top(); pq.pop();
            vp[i][0] = pq.top(); pq.pop(); 
            if(pq1.size() < 6) pq1.push({vp[i][0].first,{i,vp[i][0].second}}); 
            else if(pq1.top().first < vp[i][0].first){
                pq1.pop(); pq1.push({vp[i][0].first,{i,vp[i][0].second}}); 
            }
        }
        vector<pair<int,pair<int,int>>> vp1; 
        while(!pq1.empty()){
            vp1.push_back(pq1.top());
            pq1.pop();
        }
        // now we have max in every row, 
        // now pic 3 rows 
        long long maxi = -1e17; 
        for(int i = 0; i < n;i++){
            for(int j = i+1; j < n;j++){
                maxi = max(maxi , findMax(i,j,vp,vp1)); 
            }
        }
        return maxi; 
    }
};
