class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        m, n = len(board), len(board[0])
        points = sorted([(board[i][j], i, j) for j in range(n) for i in range(m)], reverse=True)
        top = []
        rows = defaultdict(int)
        cols = defaultdict(int)
        for i in range(len(points)):
            if rows[points[i][1]] < 3 and cols[points[i][2]] < 3:
                top.append(points[i])
                rows[points[i][1]] += 1
                cols[points[i][2]] += 1
            if len(top) == 9:
                break

        #print(top)


        res = -float('inf')
        for i in range(9):
            for j in range(9):
                for k in range(9):
                    rows = [top[i][1], top[j][1], top[k][1]]
                    cols = [top[i][2], top[j][2], top[k][2]]
                    #print(rows)
                    #print(cols)
                    if len(set(rows)) == len(rows) and len(set(cols)) == len(cols):
                        res = max(res, top[i][0]+top[j][0]+top[k][0])
        return res
