/**
 * @param {number[][]} board
 * @return {number}
 */
var maximumValueSum = function(board) {
  const m = board.length;
  const n = board[0].length;
  const arr = [];
  for (let r = 0; r < m; r++) {
    for (let c = 0; c < n; c++) {
      arr.push([board[r][c], r, c]);
    }
  }
  arr.sort((a,b) => b[0] - a[0]);
  const l = arr.length;
  let res = -Infinity;
  for (let i = 0; i < l - 2; i++) {
    const [a, r1, c1] = arr[i];
    if (a * 3 <= res) break;
    for (let j = i + 1; j < l - 1; j++) {
      const [b, r2, c2] = arr[j];
      if (r2 === r1 || c2 === c1) continue;
      if (a + 2 * b <= res) break;
      for (let k = j + 1; k < l; k++) {
        const [c, r3, c3] = arr[k];
        if (r3 === r1 || r3 === r2 || c3 === c1 || c3 === c2) continue;
        if (a + b + c <= res) break;
        res = Math.max(res, a + b + c);
      }
    }
  }
  return res;
};
