class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        ROWS, COLS = len(board), len(board[0])
        pos = []
        for r in range(ROWS):
            for c in range(COLS):
                pos.append((r, c))
        pos.sort(key = lambda p: board[p[0]][p[1]], reverse = True)
        res = float('-inf')
        for i in range(ROWS*COLS):
            if res > board[pos[i][0]][pos[i][1]] * 3:
                    break
            for j in range(i+1, ROWS*COLS):
                if res > board[pos[i][0]][pos[i][1]] + board[pos[j][0]][pos[j][1]] * 2:
                    break
                if (pos[i][0] == pos[j][0] or pos[i][1] == pos[j][1]):
                    continue
                for k in range(j+1, ROWS*COLS):
                    if (pos[j][0] == pos[k][0] or pos[i][0] == pos[k][0] or
                        pos[j][1] == pos[k][1] or pos[i][1] == pos[k][1]):
                        continue
                    total = board[pos[i][0]][pos[i][1]] + board[pos[j][0]][pos[j][1]] + board[pos[k][0]][pos[k][1]]
                    if total > res: res = total
                    else: break
        return res
