class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        vector<pair<int,int>> vec;

        int n=board.size();
        int m=board[0].size();

        for(int i=0;i<n;i++)
        {
            for(int j=0;j<m;j++)
            {
                vec.push_back({board[i][j],i*m+j});
            }
        }

        sort(vec.begin(),vec.end());
        reverse(vec.begin(),vec.end());

        long long ans=-4000000000000;
        int s=min(500,(int)vec.size());

        for(int i=0;i<s;i++)
        {
            for(int j=i+1;j<s;j++)
            {
                for(int k=j+1;k<s;k++)
                {
                    int r1=vec[i].second/m;
                    int c1=vec[i].second%m;
                    int r2=vec[j].second/m;
                    int c2=vec[j].second%m;
                    int r3=vec[k].second/m;
                    int c3=vec[k].second%m;

                    if((r1!=r2) && (r2!=r3) && (r3!=r1))
                    {
                        if((c1!=c2) && (c2!=c3) && (c3!=c1))
                        {
                            long long temp=vec[i].first;
                            temp+=vec[j].first;
                            temp+=vec[k].first;

                            ans=max(ans,temp);
                        }
                    }
                }
            }
        }

        return ans;

    }
};
