class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n=board.size();
        int m=board[0].size();
        long long ans=-1e10;

        set<pair<int,pair<int,int>>> allrow;
        for(int i=0;i<n;i++)
        {
            vector<pair<int,pair<int,int>>> row;
            for(int j=0;j<m;j++)
            {
                row.push_back(make_pair(board[i][j],make_pair(i,j)));
            }
            sort(row.begin(),row.end());
            reverse(row.begin(),row.end());
            for(int j=0;j<3;j++)allrow.insert(row[j]);
        }
       // sort(allrow.begin(),allrow.end());

        vector<pair<int,pair<int,int>>> all;
        for(int j=0;j<m;j++)
        {
            vector<pair<int,pair<int,int>>> col;
            for(int i=0;i<n;i++)
            {
                col.push_back(make_pair(board[i][j],make_pair(i,j)));
            }
            sort(col.begin(),col.end());
            reverse(col.begin(),col.end());
            for(int i=0;i<3;i++){
                if(allrow.find(col[i])!=allrow.end())all.push_back(col[i]);}
        }
        sort(all.begin(),all.end());
        reverse(all.begin(),all.end());
        //cout<<all.size()<<endl;
        //for(auto x:all)cout<<x.first<<" "<<x.second.first<<" "<<x.second.second<<" "<<endl;
        for(int i=0;i<9&&i<all.size();i++)
        {
            for(int j=0;j<9&&j<all.size();j++)
            {
                for(int k=0;k<9&&k<all.size();k++)
                {

                    if(all[i].second.first==all[j].second.first||all[j].second.first==all[k].second.first||all[k].second.first==all[i].second.first)continue;
                                        if(all[i].second.second==all[j].second.second||all[j].second.second==all[k].second.second||all[k].second.second==all[i].second.second)continue;
                    ans=max(ans,1LL*all[i].first+all[j].first+all[k].first);
                }
            }
        }
        return ans;
    }
};
