#define pii pair<int, int>
#define f first
#define s second

class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size(), m = board[0].size();
        vector<vector<pair<int, pii>>> a(n, vector<pair<int, pii>>{});
        vector<pair<int, pii>> vals;
        for(int i=0; i<n; i++) {
            vector<pair<int, pii>> temp;
            for(int j=0; j<m; j++) {
                temp.push_back({board[i][j], {i, j}});
            }
            sort(temp.rbegin(), temp.rend());
            a[i] = {temp[0], temp[1], temp[2]};
            for(auto& it: a[i]) {
                vals.push_back(it);
            }
        }

        sort(vals.rbegin(), vals.rend());
        map<int, vector<pair<int, pii>>> map1;
        int keys = 0;
        for(auto& val: vals) {
            if (keys == 7 && map1.count(val.s.s) == 0) {
                continue;
            }
            if (map1.count(val.s.s) == 0) {
                keys++;
            }
            map1[val.s.s].push_back(val);
        }

        long long ans = INT64_MIN;

        for(int i=0; i<n; i++) {
            for(int j=i+1; j<n; j++) {
                for(auto& vali: a[i]) {
                    for(auto& valj: a[j]) {
                        if (vali.s.s == valj.s.s) {
                            continue;
                        }

                        for(auto& it: map1) {
                            if (it.f == vali.s.s || it.f == valj.s.s) {
                                continue;
                            }
                            for(auto& valk: it.s) {
                                if (valk.s.f != i && valk.s.f != j) {
                                    ans = max(ans, 1LL * vali.f + 1LL * valj.f + 1LL * valk.f);
                                    break;
                                }
                            }
                        }
                    }
                }
            }
        }

        return ans;
    }
};
