import heapq
class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        L = []
        n =len(board)
        m = len(board[0])
        xs = [[] for i in range(n)]
        ys = [[] for j in range(m)]
        for i in range(n):
            for j in range(m):
                v = board[i][j]
                xs[i].append([v, j])
                ys[j].append(v)
        for i in range(n):
            xs[i].sort()
        for j in range(m):
            ys[j].sort()
        y_count = [0 for  j in range(m)]
        answer =-1*float('inf')
        L =  []
        for i in range(n):
            for j in range(3):
                if len(xs[i]) > 0:
                    v1, j1= xs[i].pop()
                    if v1 >= ys[j1][max(0, len(ys[j1])-3)] and y_count[j1] < 5:
                        L.append((v1,i,j1))
                        y_count[j1]+=1
        m2 = len(L)
        L.sort(reverse=True)
        for i in range(m2):
            for j in range(i+1, m2):
                for k in range(j+1, m2):
                    v1, i1,j1=L[i]
                    v2, i2, j2=L[j]
                    v3,i3,j3 = L[k]
                    if i1 != i2 and i2 !=i3 and  i1 != i3 and j1!=j2 and j1 != j3 and j2 !=j3:
                        answer =max(answer, v1+v2+v3)
                        break
        return answer
